import React from 'react'
import { NavLink } from 'react-router-dom'
import Cookies from 'js-cookie'
import './Banner.css'

const Banner = ({handleLogout, firstName, isLoggedIn}) => {
    const firstNameFromCookies = Cookies.get('firstName')

    return(
        <div className='banner'>
            <div className='bannerContent'>
                <img src='/goose.svg' alt='Goose' width='50px'/>
                <h1>Loosey-Goosey</h1>
                <NavLink to="/">Home</NavLink>
                {isLoggedIn && <NavLink to="/calendar">Calendar</NavLink>}
                <p>{firstNameFromCookies ? "Welcome, " + firstNameFromCookies + "!": ""}</p>
                {!isLoggedIn ? (
                    <NavLink to="/login">Login</NavLink>
                ) : (
                    <button color='#C87037' onClick={handleLogout}>Logout</button>
                )}
            </div>
        </div>
    )
}

export default Banner
